body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f4f4f4;
 }
 header {
    background-color: #333;
    color: white;
    padding: 10px;
 }
 header nav ul {
    list-style: none;
    display: flex;
    justify-content: space-around;
 }
 header nav ul li {
    padding: 10px;
 }
 header nav ul li a {
    color: white;
    text-decoration: none;
 }
 main {
    padding: 20px;
 }
 #intro {
    text-align: center;
 }
 .projects {
    display: flex;
    flex-wrap: wrap;
 }
 .project {
    background-color: white;
    border: 1px solid #ccc;
    margin: 10px;
    padding: 20px;
    width: 30%;
 }
